import React, { useState } from 'react';
import axios from 'axios';
import "../style/App1.css"

function App1() {
    const [text, setText] = useState('');
    const [score, setScore] = useState(null);
    const [history, setHistory] = useState([]);
    const [submitText, setSubmitText] = useState('');

    const analyzeSentiment = () => {
        // ここに感情分析のロジックを実装します
        // ダミーのスコアを設定
        const dummyScore = Math.random() * 100;
        setScore(dummyScore);
        setHistory([...history, { text, score: dummyScore }]);
    };

    // テキスト情報をJSONに変換
    const jsonText = JSON.stringify({ sentiment_text: text });
    setSubmitText(jsonText);

    // axiosを使用してエンドポイントにPOSTリクエストを送信
    // axios.post('YOUR_ENDPOINT_URL', jsonText, {
    //     headers: {
    //         'Content-Type': 'application/json',
    //     },
    // })
    //     .then(response => {
    //         // リクエストが成功した場合の処理
    //         console.log('API Response:', response.data);
    //     })
    //     .catch(error => {
    //         // エラーが発生した場合の処理
    //         console.error('API Error:', error);
    //     });

    return (
        <div className="app1">
            <div className="input-form">
        <textarea
            value={text}
            onChange={(e) => setText(e.target.value)}
            placeholder="テキストを入力..."
        />
                <button onClick={analyzeSentiment}>Analyze</button>
                <div className="score">
                    {score !== null && <p>Sentiment Score: {score.toFixed(2)}</p>}
                </div>
            </div>
            <div className="display-area">
                <h2>History</h2>
                <ul>
                    {history.map((item, index) => (
                        <li key={index}>{item.text} - Score: {item.score.toFixed(2)}</li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default App1;
